$(document).ready(function(){$(".JS").hide().fadeIn(1300);$(".NQ").hide().fadeIn(2200);$(".forHeader").load("header.html");$(".forFooter").load("footer.html");var num=25;var tag="#JS";for(var i=1;i<=num;i++){$(".previews").append("<a class='JS"+i+"' href='"+tag+i+"'><button class='btn for_center'><img class='centered' src='images/house_JS"+('0'+i).slice(-2)+"-min.jpg'></button></a>");} for(var i=1;i<=num;i++){$(".photos").append("<img id='JS"+i+"' class='centered slider' src='images/house_JS"+('0'+i).slice(-2)+"-min.jpg' >");} $("a").click(function(){for(var i=1;i<=num;i++){if($(this).is(".JS"+i)){$("#dialog, #JS"+i).show();}}});$(".close").click(function(){$("#dialog, .slider").hide();});$(".slider").click(function(){var i;for(var i=1;i<=num;i++){if($(this).is("#JS"+i)){$("#JS"+(i+1)).show();$("#JS"+i).hide();}else if($(this).is("#JS25")){$("#JS1").show();$("#JS25").hide();}}});$(".enlarge").append("<div class='hint'></div>");$(".hint").html("點圖看下一張");for(var i=1;i<=12;i++){tag="#NQ";$(".roomA").append("<a class='NQ"+i+"' href='"+tag+i+"'><button class='btn for_center'><img class='centered' src='images/house_NQ"+('0'+i).slice(-2)+"-min.jpg'></button></a>");} for(var i=13;i<=22;i++){tag="#NQ";$(".roomB").append("<a class='NQ"+i+"' href='"+tag+i+"'><button class='btn for_center'><img class='centered' src='images/house_NQ"+('0'+i).slice(-2)+"-min.jpg'></button></a>");} for(var i=1;i<=(num-3);i++){$(".photo_NQ").append("<img id='NQ"+i+"' class='centered slider' src='images/house_NQ"+('0'+i).slice(-2)+"-min.jpg' >");} $("a").click(function(){for(var i=1;i<=(num-3);i++){if($(this).is(".NQ"+i)){$("#dialog, #NQ"+i).show();}}});$(".slider").click(function(){var i;for(var i=1;i<=(num-3);i++){if($(this).is("#NQ"+i)){$("#NQ"+(i+1)).show();$("#NQ"+i).hide();}else if($(this).is("#NQ22")){$("#NQ1").show();$("#NQ22").hide();}}});});